    public static void hearbeat() {
        new Thread() {
            @Override
            public void run() {
                super.run();
                while (true) {
                    FsPipelineJNI.postHeartbeat();
                    try {
                        Thread.sleep(1 * 60 * 1000);
                    } catch (Throwable throwable) {
                        throwable.printStackTrace();
                    }
                }
            }
        }.start();
    }

